                client.SendPacket(new Packet(PacketType.StartMsg,
                    Console.OutputEncoding.GetBytes("Hello ")));
                client.SendPacket(new Packet(PacketType.EndMsg,
                    Console.OutputEncoding.GetBytes("World!")));
                string filePath = "C:\\Users\\m4ss\\source\\repos\\mssevov18\\ClientServerAPI\\ClientServer-V2\\Client\\testfile.txt";
                client.SendFile(new FileStruct("testfile.txt", File.ReadAllBytes(filePath)));
            }
        }
    }
}
